import React from 'react';
import '../../styles/components/about.css';

interface DetailBox {
  icon: string;
  title: string;
  items: string[];
}

const About: React.FC = () => {
  const details: DetailBox[] = [
    {
      icon: "./assets/hobbies.png",
      title: "Hobbies & Interests",
      items: ["Fitness & Weightlifting", "Graphic Design", "Music"]
    },
    {
      icon: "./assets/education.png",
      title: "Extracurriculars",
      items: ["Forge Software Team Member", "Disrupt Consulting Associate", "Finnovate Co-Director"]
    }
  ];

  return (
    <section id="about">
      <p className="section__text__p1">Get To Know More</p>
      <h1 className="title">About Me</h1>
      <div className="section-container">
        <div className="section__pic-container">
          <img
            src="./assets/about-pic.png"
            alt="Profile picture"
            className="about-pic"
            loading="lazy"
          />
        </div>
        <div className="about-details-container">
          <div className="about-containers">
            {details.map((detail, index) => (
              <div key={index} className="details-container">
                <img
                  src={detail.icon}
                  alt={`${detail.title} icon`}
                  className="icon"
                  loading="lazy"
                />
                <h3>{detail.title}</h3>
                <br />
                <p>{detail.items.join("<br />")}</p>
              </div>
            ))}
          </div>
          <div className="text-container">
            <p>
              Hi, I'm Arsh Jafri, a <b>Computer Science and Economics</b> combined major 
              in <b>Northeastern University's Honors Program</b>. I'm passionate about leveraging 
              technology to solve real-world problems, whether through <b>software development, 
                data science, or research</b>.
              <br /><br />
              I've worked on projects involving <b>frontend development, data analysis, and fintech 
              applications,</b> while also contributing to entrepreneurial and research-driven initiatives. 
              Beyond coursework, I'm actively involved in Northeastern's consulting and tech communities, 
              applying my skills through <b>pro bono consulting, research roles, and leadership opportunities</b>. 
              <br /><br />
              Outside of work and academics, I love going to the <b>gym, graphic design, listening to Drake, and 
                rewatching Modern Family</b>. Keep scrolling to learn more about my work and extracurriculars!
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
